mapper.update:
/* TreviewMapper.xml id="updateTreview" */
UPDATE REVIEW_BOARD
SET
	REVIEWTITLE = #{reviewtitle}
	, REVIEWCONTENT = #{reviewcontent}
	, ORDERNO = #{orderno}

		, IMG_PATH = #{img_path}


		, IMG_KEY = #{img_key}


		, IMG_NAME = #{img_name}

WHERE
	REVIEWNO = #{reviewno}

mapper.insert:
/* TreviewMapper.xml id="insertTreview" */
INSERT INTO REVIEW_BOARD
	(REVIEWNO
		, REVIEWTITLE
		, REVIEWCONTENT
		, REVIEWDATE
		, ID
		, ORDERNO

			, IMG_PATH


			, IMG_KEY


			, IMG_NAME

	)
VALUES
	(#{reviewno}
		, #{reviewtitle}
		, #{reviewcontent}
		, SYSDATE
		, (SELECT ID FROM MEMBER WHERE ROWNUM = 1)
		, #{orderno}

			, #{img_path}


			, #{img_key}


			, #{img_name}

	)

/* TreviewMapper.xml id="insertTreviewReply" */
INSERT INTO REVIEW_REPLY
	(REPLYNO, REPLIER, REPLY, REVIEWNO, CREATED_DT)
VALUES
	((SELECT NVL(MAX(REPLYNO), 0) + 1 FROM REVIEW_REPLY)
		, (SELECT ID FROM MEMBER WHERE ROWNUM = 1)
		, #{reply}
		, #{reviewno}
		, SYSDATE
	)

mapper.update:
/* TreviewMapper.xml id="insertTreviewReply" */
UPDATE REVIEW_REPLY
SET
	REPLY = #{reply}
WHERE
	REPLYNO = #{replyno}

mapper.delete:
/* TreviewMapper.xml id="deleteTreviewReply" */
DELETE FROM REVIEW_REPLY
WHERE
	REPLYNO = #{replyno}

/* TreviewMapper.xml id="deleteTreviewReplyAll" */
DELETE FROM REVIEW_REPLY
WHERE
	REVIEWNO = #{reviewno}

/* TreviewMapper.xml id="deleteTreview" */
DELETE FROM REVIEW_BOARD
WHERE
	REVIEWNO = #{reviewno}
